(function() {
    'use strict';

    Rest.$inject = ["Restangular", "BACKEND_SERVICE_DOMAIN"];
    Auth.$inject = ["Restangular", "BACKEND_SERVICE_DOMAIN"];
    angular
        .module('zaya')
        .factory('Rest', Rest)
        .factory('Auth', Auth)

    function Rest(Restangular, BACKEND_SERVICE_DOMAIN) {
        return Restangular.withConfig(function(RestangularConfigurer) {
            RestangularConfigurer.setBaseUrl(BACKEND_SERVICE_DOMAIN+'/api/v1');
            RestangularConfigurer.setRequestSuffix('/');
        });
    }
    function Auth(Restangular,BACKEND_SERVICE_DOMAIN){
      var rest_auth = Restangular.withConfig(function(RestangularConfigurer) {
          RestangularConfigurer.setBaseUrl(BACKEND_SERVICE_DOMAIN+'/rest-auth');
          RestangularConfigurer.setRequestSuffix('/');
          RestangularConfigurer.setDefaultHeaders({
            'Content-Type':'application/x-www-form-urlencoded',
          });
      });
      return {
        login : function(user_credentials, success, failure){
          rest_auth.all('login').post($.param(user_credentials)).then(function(response){
            localStorage.setItem('Authorization',response.key);
            success();
          },function(){
            failure();
          })
        },
        logout : function(success,failure){
          rest_auth.all('logout').post().then(function(response){
            localStorage.removeItem('Authorization');
            success();
          },function(error){
            failure();
          })
        },
        signup : function(user_credentials,success,failure){
          rest_auth.all('registration').post($.param(user_credentials),success,failure).then(function(response){
            localStorage.setItem('Authorization',response.key);
            success();
          },function(response){
            failure();
          })
        },
        reset : function (email,type,success,failure) {
          type=='password' && rest_auth.all('password').all('reset').post(email);
          type=='username' && rest_auth.all('username').all('reset').post(email);
        },
        isAuthorised : function(){
          return localStorage.Authorization;
        }
      }
    }
})();

(function() {
  'use strict';

  mainRoute.$inject = ["$stateProvider", "$urlRouterProvider"];
  angular
    .module('zaya')
    .config(mainRoute);

  function mainRoute($stateProvider, $urlRouterProvider) {

    $stateProvider
      //authentication
      .state('auth',{
        url : '/auth',
        abstract : true,
        template : '<ion-nav-view></ion-nav-view>'
      })
      .state('auth.main',{
        url : '/main',
        templateUrl : 'modules/auth/auth.view.main.html'
      })
      .state('auth.signin', {
        url: '/signin',
        templateUrl: 'modules/auth/auth.view.signin.html',
        controller : 'authController as authCtrl'
      })
      .state('auth.signup',{
        url : '/signup',
        templateUrl : 'modules/auth/auth.view.signup.html',
        controller : 'authController as authCtrl'
      })
      .state('auth.forgot',{
        url : '/forgot',
        templateUrl : 'modules/auth/auth.view.forgot.html'
      })
      //profile
      .state('profile',{
        url : '/profile',
        abstract : true,
        template : '<ion-nav-view></ion-nav-view>'
      })
      .state('profile.personalise',{
        url : '/personalise',
        abstract : true,
        templateUrl : 'modules/profile/profile.view.personalise.main.html',
      })
      .state('profile.personalise.usertype',{
        url : '/usertype',
        templateUrl : 'modules/profile/profile.view.personalise.usertype.html'
      })
      .state('profile.personalise.usersubject',{
        url : '/usersubject',
        templateUrl : 'modules/profile/profile.view.personalise.usersubject.html'
      })
      .state('profile.tabs',{
        url : '/main',
        abstract : true,
        templateUrl : 'modules/profile/profile.view.tabs.html'
      })
      .state('profile.tabs.groups',{
        url : '/groups',
        views : {
          'group-tab' : {
            templateUrl : 'modules/profile/profile.view.group.html'
          }
        }
      })
      .state('profile.tabs.badges',{
        url : '/badges',
        views : {
          'badge-tab':{
            templateUrl : 'modules/profile/profile.view.badge.html'
          }
        }
      })
      //landing
      .state('landing',{
        url : '/landing',
        abstract : true,
        templateUrl : 'modules/landing/landing.view.main.html'
      })
      .state('landing.playlist',{
        url : '/playlist',
        templateUrl : 'modules/playlist/playlist.view.html'
      })
      .state('landing.home',{
        url : '/home',
        templateUrl : 'modules/home/home.view.html'
      })
      .state('landing.result',{
        url : '/result',
        templateUrl : 'modules/result/result.view.html'
      })
      .state('landing.search',{
        url : '/search',
        templateUrl : 'modules/search/search.view.html'
      })


    $urlRouterProvider.otherwise('/auth/main');
  }
})();

(function(){
  'use strict';

  runConfig.$inject = ["$ionicPlatform"];
  angular
    .module('zaya')
    .run(runConfig);

  function runConfig($ionicPlatform) {
    $ionicPlatform.ready(function() {
      if(window.cordova && window.cordova.plugins.Keyboard) {
        // Hide the accessory bar by default (remove this to show the accessory bar above the keyboard
        // for form inputs)
        cordova.plugins.Keyboard.hideKeyboardAccessoryBar(true);

        // Don't remove this line unless you know what you are doing. It stops the viewport
        // from snapping when text inputs are focused. Ionic handles this internally for
        // a much nicer keyboard experience.
        cordova.plugins.Keyboard.disableScroll(true);
      }
      if(window.StatusBar) {
        StatusBar.styleDefault();
      }
    });
  }

})();

